mapper.select:
select

    distinct


from customer




    order by ${orderByClause}


    limit #{limitStart} , #{limitEnd}

SELECT a.driving_record FROM card_port_data a
WHERE  a.license_num = #{license,jdbcType=VARCHAR}
AND    a.`status` = '01'

select count(cs.id) as totalNum FROM sys_month sm LEFT JOIN customer cs ON sm.monthNum = MONTH(cs.create_time)
and DATE_FORMAT(create_time,'%Y') = #{year}
and DATE_FORMAT(create_time,'%Y') = #{year}
GROUP BY sm.monthNum ASC
